package basicSwapNumber;

public class VikramSalary {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int salary=80000;
int tax=20;
int totalTax;
System.out.println("Vikram salary is Rs."+salary);
System.out.println("Incometax on salary is "+tax+"%");

totalTax=(salary*tax)/100;

System.out.println("Total amount of tax paid by Vikram "+"Rs."+totalTax);

	}

}
